import React, { useState, useEffect } from "react";
import Carousel from "react-bootstrap/Carousel";
import * as Icons from "phosphor-react";
import "../../css/curatedbrands.css";
import data from "../../Data/CuratedDetailed/data";
import { Link } from "react-router-dom";
function MainDetailCurated(props) {
  const [propsData, setData] = useState("");
  const [FinalData, setFinalData] = useState([]);
  useEffect(() => {
    setData(props.location.state.d);

    let result = [];
    for (let i = 0; i < data.length; i++) {
      result.push(data[i].src);
    }
    Promise.all(result).then((response) => {
      let finalData = response.map((item, index) => {
        var res = item.split(".");
        var format = res[res.length - 1];
        if (format == "png" || format == "jpg" || format == "jpeg") {
          return {
            src: item,
            video: false,
          };
        } else {
          return {
            src: item,
            video: true,
          };
        }
      });
      setFinalData(finalData);
    });
  }, []);

  return (
    <>
      {/* 3D */}
      <div
        className="max-w-6xl mt-40 text-center mx-auto px-4 sm:px-6 "
        data-aos="zoom-y-out"
      >
        <span className="font-Lobster italic text-5xl font-normal">
          Spinin Records
        </span>
      </div>
      <div className="max-w-8xl mt-10 text-center   mx-auto px-4 sm:px-6">
        <Carousel className="mt-20 shadow-2xl">
          <Carousel.Item className="changeColor">
            <div>
              <img
                src={propsData.src}
                class="object-cover  w-full ..."
                style={{ height: 430 }}
              />
            </div>

            <Carousel.Caption style={{ paddingBottom: 90 }}>
              <div className="row">
                <div className="col-md-3"></div>
                <div className="col-md-6 transparent">
                  <div style={{ paddingTop: 50, paddingBottom: 50 }}>
                    <h1>First slide label</h1>
                    <p>
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                    </p>
                  </div>
                </div>
              </div>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      </div>

      <div>
        <div className="max-w-6xl   mx-auto px-4 sm:px-6">
          {/* Top area: Blocks */}
          <div className="px-6  py-8 md:py-12">
            {/* 1st block */}
            <div className="md:flex md:items-center md:justify-between py-4 md:py-8 border-t border-gray-200">
              {/* Social links */}
              <ul className="flex mb-4 md:order-1 md:ml-4 md:mb-0">
                <li>
                  <Link
                    to="#"
                    className="flex justify-center items-center text-gray-600 hover:text-gray-900 bg-white hover:bg-white-100 rounded-full shadow transition duration-150 ease-in-out"
                    aria-label="Twitter"
                  >
                    <svg
                      className="w-8 h-8 fill-current"
                      viewBox="0 0 32 32"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M24 11.5c-.6.3-1.2.4-1.9.5.7-.4 1.2-1 1.4-1.8-.6.4-1.3.6-2.1.8-.6-.6-1.5-1-2.4-1-1.7 0-3.2 1.5-3.2 3.3 0 .3 0 .5.1.7-2.7-.1-5.2-1.4-6.8-3.4-.3.5-.4 1-.4 1.7 0 1.1.6 2.1 1.5 2.7-.5 0-1-.2-1.5-.4 0 1.6 1.1 2.9 2.6 3.2-.3.1-.6.1-.9.1-.2 0-.4 0-.6-.1.4 1.3 1.6 2.3 3.1 2.3-1.1.9-2.5 1.4-4.1 1.4H8c1.5.9 3.2 1.5 5 1.5 6 0 9.3-5 9.3-9.3v-.4c.7-.5 1.3-1.1 1.7-1.8z" />
                    </svg>
                  </Link>
                </li>
                <li className="ml-4">
                  <Link
                    to="#"
                    className="flex justify-center items-center text-gray-600 hover:text-gray-900 bg-white hover:bg-white-100 rounded-full shadow transition duration-150 ease-in-out"
                    aria-label="Github"
                  >
                    <svg
                      className="w-8 h-8 fill-current"
                      viewBox="0 0 32 32"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M16 8.2c-4.4 0-8 3.6-8 8 0 3.5 2.3 6.5 5.5 7.6.4.1.5-.2.5-.4V22c-2.2.5-2.7-1-2.7-1-.4-.9-.9-1.2-.9-1.2-.7-.5.1-.5.1-.5.8.1 1.2.8 1.2.8.7 1.3 1.9.9 2.3.7.1-.5.3-.9.5-1.1-1.8-.2-3.6-.9-3.6-4 0-.9.3-1.6.8-2.1-.1-.2-.4-1 .1-2.1 0 0 .7-.2 2.2.8.6-.2 1.3-.3 2-.3s1.4.1 2 .3c1.5-1 2.2-.8 2.2-.8.4 1.1.2 1.9.1 2.1.5.6.8 1.3.8 2.1 0 3.1-1.9 3.7-3.7 3.9.3.4.6.9.6 1.6v2.2c0 .2.1.5.6.4 3.2-1.1 5.5-4.1 5.5-7.6-.1-4.4-3.7-8-8.1-8z" />
                    </svg>
                  </Link>
                </li>
                <li className="ml-4">
                  <Link
                    to="#"
                    className="flex justify-center items-center text-gray-600 hover:text-gray-900 bg-white hover:bg-white-100 rounded-full shadow transition duration-150 ease-in-out"
                    aria-label="Facebook"
                  >
                    <svg
                      className="w-8 h-8 fill-current"
                      viewBox="0 0 32 32"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M14.023 24L14 17h-3v-3h3v-2c0-2.7 1.672-4 4.08-4 1.153 0 2.144.086 2.433.124v2.821h-1.67c-1.31 0-1.563.623-1.563 1.536V14H21l-1 3h-2.72v7h-3.257z" />
                    </svg>
                  </Link>
                </li>
              </ul>

              {/* Copyrights note */}
              <div className="text-sm text-gray-600 mr-4">
                Made by{" "}
                <a
                  className="text-blue-600 hover:underline"
                  href="https://cruip.com/"
                >
                  Cruip
                </a>
                . All rights reserved.
              </div>
            </div>
          </div>
        </div>
      </div>

      <section className="relative mt-40 px-20">
        {/* Section background (needs .relative class on parent and next sibling elements) */}

        <div className="relative max-w-8xl   sm:px-6">
          <div>
            {/* Items */}
            <div className="max-w-sm mx-auto grid gap-6 md:grid-cols-3 lg:grid-cols-3 md:max-w-2xl lg:max-w-none">
              {/* 1st item */}

              {FinalData.map((d, key) => {
                return (
                  <Link
                    className="mb-20"
                    to={{ pathname: `/artworkdetailed`, state: { d } }}
                  >
                    <div className="carousel  shadow-2xl border-4 border-white flex flex-col bg-white sm:mb-2 ">
                      {d.video ? (
                        <>
                          <video autoPlay muted loop>
                            <source src={d.src} type="video/mp4" />
                          </video>
                          <div
                            style={{
                              position: "absolute",
                              top: "8px",
                              right: "20px",
                            }}
                          >
                            <Icons.VideoCamera
                              size={20}
                              color="white"
                              weight="fill"
                            />
                          </div>
                        </>
                      ) : (
                        <>
                          <img src={d.src} />
                        </>
                      )}
                    </div>
                  </Link>
                );
              })}
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default MainDetailCurated;
