import React, { Component, useState, useEffect } from "react";

import { BrowserRouter as Router } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap";
import "../css/header.css";
import logoChimera from "../images/logoChimera.png";
import { makeStyles } from "@material-ui/core/styles";
import * as Icons from "phosphor-react";

function Header() {
  const [top, setTop] = useState(true);
  const [open, setOpen] = React.useState(false);

  const handleToggle = () => {
    setOpen((prevOpen) => !prevOpen);
  };

  // detect whether user has scrolled the page down by 10px
  useEffect(() => {
    const scrollHandler = () => {
      window.pageYOffset > 10 ? setTop(false) : setTop(true);
    };
    window.addEventListener("scroll", scrollHandler);
    return () => window.removeEventListener("scroll", scrollHandler);
  }, [top]);
  return (
    <Router>
      <header
        className={`fixed  px-4 w-full bg-white-500 z-30 md:bg-opacity-90 transition duration-300 ease-in-out ${
          !top && " bg-red-100 blur shadow-lg"
        }`}
      >
        <Navbar expand="lg">
          <Navbar.Brand href="/">
            <img src={logoChimera} style={{ width: 130 }} />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link href="/editorial">Editorial</Nav.Link>
              <Nav.Link href="/discover">Discover</Nav.Link>
              <Nav.Link href="/market">Market</Nav.Link>
              <Nav.Link href="/news">News</Nav.Link>
              <Nav.Link href="/activity">Activity</Nav.Link>
              {localStorage.getItem("walletAddress") ? (
                // <Nav.Link href="/editorial">Editorial</Nav.Link>
                <>
                  <div class="relative inline-block text-left">
                    <div>
                      <button
                        type="button"
                        class="inline-flex justify-center w-full rounded-md   px-4 py-2  text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-100 focus:ring-indigo-500"
                        id="options-menu"
                        aria-expanded="true"
                        aria-haspopup="true"
                        onClick={handleToggle}
                      >
                        <Icons.UserCircle size={38} color="black" />
                        <Icons.CaretDown size={25} color="black" />
                      </button>
                    </div>
                    {open ? (
                      <>
                        <div
                          class="origin-top-right absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none"
                          role="menu"
                          aria-orientation="vertical"
                          aria-labelledby="options-menu"
                        >
                          <div class="py-1" role="none">
                            <a
                              href="#"
                              class="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                              role="menuitem"
                            >
                              Account settings
                            </a>
                            <a
                              href="#"
                              class="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                              role="menuitem"
                            >
                              Support
                            </a>
                            <a
                              href="#"
                              class="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                              role="menuitem"
                            >
                              License
                            </a>
                            <form method="POST" action="#" role="none">
                              <button
                                type="submit"
                                class="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                                role="menuitem"
                              >
                                Sign out
                              </button>
                            </form>
                          </div>
                        </div>
                      </>
                    ) : null}
                  </div>
                </>
              ) : (
                <Nav.Link href="/signin">SignIn</Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </header>
    </Router>
  );
}

export default Header;
