import React, { useEffect } from "react";
import { Switch, Route, useLocation, NotFoundRoute } from "react-router-dom";
  import Web3 from "web3";
import "./css/style.scss";
import "./App.css";
import AOS from "aos";
import { focusHandling } from "cruip-js-toolkit";
import { useHistory } from "react-router-dom";
import Home from "./pages/Home";
import Discover from "./pages/Discover";
import Market from "./pages/Market";
import Activity from "./pages/Activity";
import SignIn from "./pages/SignIn";
import Collection from "./pages/Collection";
import Dashboard from "./pages/Dashboard";
import ArtWorkDetailed from "./pages/ArtWorkDetailed";
import CuratedBrands from "./pages/CuratedBrands";
import DetailedCurated from "./pages/detailedCurated";
import CreateDigitalArt from "./pages/CreateDigitalArt";
import CreatePhysicalArt from "./pages/CreatePhysicalArt";
let web3;
let accounts;
function App() {
  const location = useLocation();
  let history = useHistory();

  useEffect(() => {
    AOS.init({
      once: true,
      disable: "phone",
      duration: 700,
      easing: "ease-out-cubic",
    });
  });
  async function metamaskConnection() {
    // web3 = new Web3(window.ethereum);
    // accounts = await web3.eth.getAccounts();
    // await window.ethereum.enable();
    // web3.eth.getAccounts(function (err, accounts) {
    //   if (err != null) console.error("An error occurred: " + err);
    //   else if (accounts.length == 0 && localStorage.getItem("walletAddress")) {
    //     console.log("User is not logged in to MetaMask");
    //     localStorage.clear();
    //     history.push("/");
    //   } else console.log("User is logged in to MetaMask");
    // });

    // if (
    //   localStorage.getItem("walletAddress") &&
    //   localStorage.getItem("walletAddress") != accounts
    // ) {
    //   localStorage.clear();
    //   history.push("/");
    // }
  }
  window.ethereum.on("accountsChanged", async function (accounts) {
    // document.location.reload();
    let acc = await web3.eth.getAccounts();
    console.log(acc);
    if (
      localStorage.getItem("walletAddress") &&
      localStorage.getItem("walletAddress") != acc
    ) {
      localStorage.clear();
      history.push("/signin");
    }
  });

  useEffect(() => {
    metamaskConnection();

    document.querySelector("html").style.scrollBehavior = "auto";
    window.scroll({ top: 0 });
    document.querySelector("html").style.scrollBehavior = "";
    focusHandling("outline");
  }, [location.pathname]); // triggered on route change

  return (
    <>
      {localStorage.getItem("walletAddress") ? (
        <>
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/discover">
              <Discover />
            </Route>
            <Route path="/market">
              <Market />
            </Route>
            <Route path="/activity">
              <Activity />
            </Route>

            <Route path="/signin">
              <SignIn />
            </Route>
            <Route path="/collection">
              <Collection />
            </Route>
            <Route path="/dashboard">
              <Dashboard />
            </Route>
            <Route path="/artworkdetailed">
              <ArtWorkDetailed />
            </Route>
            <Route path="/curated-brands">
              <CuratedBrands />
            </Route>
            {/* <Route path="/curated-detailed/:id"> */}
            <Route path="/curated-detailed">
              <DetailedCurated />
            </Route>
            <Route path="/create-digitalart">
              <CreateDigitalArt />
            </Route>
            <Route path="/create-physicalart">
              <CreatePhysicalArt />
            </Route>
          </Switch>
        </>
      ) : (
        <>
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/discover">
              <Discover />
            </Route>
            <Route path="/market">
              <Market />
            </Route>
            <Route path="/activity">
              <Activity />
            </Route>

            <Route path="/signin">
              <SignIn />
            </Route>
            <Route path="/artworkdetailed">
              <ArtWorkDetailed />
            </Route>
            <Route path="/curated-brands">
              <CuratedBrands />
            </Route>
            {/* <Route path="/curated-detailed/:id"> */}
            <Route path="/curated-detailed">
              <DetailedCurated />
            </Route>
          </Switch>
        </>
      )}
    </>
  );
}

export default App;
