import React, { useState, useEffect } from "react";
import Header from "../partials/Header";
import Footer from "../partials/Footer";
import Web3 from "web3";
let web3;

function Dashboard() {
  useEffect(async () => {
    web3 = new Web3(window.ethereum);
    await window.ethereum.enable();

    web3.eth.getBalance(
      localStorage.getItem("account"),
      function (err, result) {
        if (err) {
          console.log(err);
        } else {
          const balance = web3.utils.fromWei(result, "ether");
          console.log(financial(balance));
        }
      }
    );
  }, []);
  function financial(x) {
    return Number.parseFloat(x).toFixed(3);
  }
  return (
    <>
      <div className="flex flex-col min-h-screen overflow-hidden">
        <Header />
        <div>
          <div className="max-w-6xl  text-center mt-32 mx-auto px-4 sm:px-6">
            {/* Top area: Blocks */}
            <div className=" gridsm:grid-cols-12 gap-6 py-20 md:py-12">
              <div className="col-span-6 text-left">
                <h1 className="font-extrabold text-black" data-aos="zoom-y-out">
                  Dashboard
                </h1>
              </div>
              <div className="col-span-6 text-left">
                <h1 className="font-extrabold text-black" data-aos="zoom-y-out">
                  Dashboard
                </h1>
              </div>
            </div>
          </div>
        </div>
        {/* Start*/}

        <Footer />
      </div>
    </>
  );
}

export default Dashboard;
