        if (netId != config.networkId) {
          localStorage.clear();
          history.push("/");
          alert("Please Select the correct network");
        }
      });
      window.ethereum.on("networkChanged", function (networkId) {
        if (networkId != config.networkId) {
          localStorage.clear();
