            <Route path="/discover">
              <Discover />
            </Route>
            <Route path="/market">
              <Market />
            </Route>
            <Route path="/activity">
              <Activity />
            </Route>

            <Route path="/signin">
              <SignIn />
            </Route>
            <Route path="/collection">
              <Collection />
            </Route>
            <Route path="/dashboard">
              <Dashboard />
            </Route>
            <Route path="/artworkdetailed">
              <ArtWorkDetailed />
            </Route>
            <Route path="/curated-brands">
              <CuratedBrands />
            </Route>
            {/* <Route path="/curated-detailed/:id"> */}
