        <>
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/discover">
              <Discover />
            </Route>
            <Route path="/market">
              <Market />
            </Route>
            <Route path="/activity">
              <Activity />
            </Route>

            <Route path="/signin">
              <SignIn />
            </Route>
            <Route path="/artworkdetailed">
              <ArtWorkDetailed />
