import React, { useState, useEffect } from "react";
import Web3 from "web3";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import { useHistory } from "react-router-dom";
import validator from "validator";
import Swal from "sweetalert2";
import axios from "axios";
import ReactLoading from "react-loading";

import config from "../../config";

const chimeraContract = require("../../contracts/Chimera.json");
let web3;
let accounts;
let chimera;
const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

function MainSignIn() {
  const history = useHistory();
  const classes = useStyles();
  const [notAccount, setNotAccount] = useState(false);
  const [email, setEmail] = useState("");
  const [accountType, setAccountType] = useState("");
  const [checkEmail, setCheckEmail] = useState(false);
  const [walletAccount, setWalletAccount] = useState("");
  const [username, setUsername] = useState("");
  const [loading, setLoading] = useState(true);
  const [signupDiv, setSignupDiv] = useState(false);
  const [emailError, setEmailError] = useState("");

  const validateEmail = (e) => {
    var email = e.target.value;

    if (validator.isEmail(email)) {
      setEmailError("");
      setEmail(e.target.value);
      setCheckEmail(true);
    } else {
      setCheckEmail(false);
      setEmailError("Enter valid Email!");
    }
  };
  async function metamaskConnection() {
    web3 = new Web3(window.ethereum);
    accounts = await web3.eth.getAccounts();
    await window.ethereum.enable();
    // setWalletAccount(accounts[0]);
  }
  useEffect(() => {
    metamaskConnection();
  }, []);
  window.ethereum.on("accountsChanged", async function (accounts) {
    // document.location.reload();

    window.location.reload();
  });

  async function ConnectWallet() {
    setLoading(true);
    accounts = await web3.eth.getAccounts();
    setWalletAccount(accounts[0]);
    chimera = await new web3.eth.Contract(chimeraContract.abi, config.Chimera);

    let obj = {
      address: accounts[0],
    };
    axios
      .post(`http://localhost:5000/api/auth`, obj)
      .then(async (res) => {
        console.log("res--->>", res);
        let result = await chimera.methods.isWhitelisted(accounts[0]).call();
        if (result == true) {
          console.log("heyTrue");
        } else if (result == false) {
          console.log("heyFalse");
        }
      })
      .catch((err) => {
        console.log("err--->>", err.response.data.msg);
        setNotAccount(true);
      });
  }

  function handleSignUp() {
    // localStorage.setItem("username", username);
    // localStorage.setItem("email", email);
    // localStorage.setItem("walletAddress", walletAccount);
    var obj = {
      name: username,
      email: email,
      address: walletAccount,
      role: accountType,
    };
    axios
      .post(`http://localhost:5000/api/users`, obj)
      .then(async (res) => {
        console.log("res--->>", res);
        await Swal.fire({
          position: "top-start",
          icon: "success",
          title: "Please wait to be whitlisted!",
          showConfirmButton: false,
          timer: 3500,
        });
        window.location.reload();
      })
      .catch((err) => {
        console.log("err--->>", err.response.data.msg);
        Swal.fire({
          icon: "error",
          title: "Oops...",
          text: err.response.data.msg,
        });
      });
  }
  function myFunction() {
    setSignupDiv(true);
    var x = document.getElementById("oops");
    if (x.style.display === "none") {
      x.style.display = "block";
    } else {
      x.style.display = "none";
    }
  }
  function validateForm() {
    return email.length > 0 && username.length > 0 && checkEmail && accountType;
  }
  const AccountType = (e) => {
    console.log(e.target.value);
    setAccountType(e.target.value);
  };
  return (
    <>
      <div>
        <div className="max-w-6xl  text-center mb-32 mt-32 mx-auto px-4 sm:px-6">
          {/* Top area: Blocks */}
          {!notAccount ? (
            <div>
              <div className=" gridsm:grid-cols-12">
