import React, { useState, useEffect } from "react";
import Header from "../partials/Header";
import Footer from "../partials/Footer";
import Web3 from "web3";
let web3;

function Dashboard() {
  useEffect(async () => {
    web3 = new Web3(window.ethereum);
    await window.ethereum.enable();

    web3.eth.getBalance(
      localStorage.getItem("account"),
      function (err, result) {
        if (err) {
          console.log(err);
        } else {
          const balance = web3.utils.fromWei(result, "ether");
          console.log(financial(balance));
        }
      }
    );
  }, []);
  function financial(x) {
    return Number.parseFloat(x).toFixed(3);
  }
  return (
    <>
      <div className="flex flex-col min-h-screen overflow-hidden">
        <Header />
        <div>
          <div className="max-w-6xl  mt-32 mx-auto px-4 sm:px-6">
            {/* Top area: Blocks */}
            <div className="grid sm:grid-cols-12  px-6 gap-8 py-8 md:py-12">
              {/* 1st block */}
              <div className="sm:col-span-12 lg:col-span-3">
                <div className="mb-2">
                  <h1
                    className="text-4xl md:text-3xl my-2 font-extrabold  tracking-tighter mb-4"
                    data-aos="zoom-y-out"
                  >
                    How it works for Artists.
                  </h1>
                </div>
              </div>

              {/* 2nd block */}
              <div className="sm:col-span-6 mt-4 md:col-span-3 lg:col-span-3">
                <h6 className="text-gray-800 text-xs font-extrabold mb-2">
                  AUTHENTICATE YOUR WORK.
                </h6>
                <p className="text-sm">
                  Digitally sign your work by creating a tokenized certificate.
                </p>
              </div>

              {/* 3rd block */}
              <div className="sm:col-span-6 mt-4 md:col-span-3 lg:col-span-3">
                <h6 className="text-gray-800 text-xs font-extrabold mb-2">
                  SET PRICES & RUN AUCTIONS.
                </h6>
                <p className="text-sm">
                  Set a price or let collectors bid on your art in the digital
                  art marketplace.
                </p>
              </div>


            </div>
          </div>
        </div>
        {/* Start*/}

        <Footer />
      </div>
    </>
  );
}

export default Dashboard;
