import React, { useState, useEffect } from "react";
import Header from "../partials/Header";
import Footer from "../partials/Footer";
import Web3 from "web3";
import * as Icons from "phosphor-react";
let web3;

function Dashboard() {
  const [WalletBalance, setWalletBalance] = useState("");
  useEffect(async () => {
    web3 = new Web3(window.ethereum);
    await window.ethereum.enable();

    web3.eth.getBalance(
      localStorage.getItem("account"),
      function (err, result) {
        if (err) {
          console.log(err);
        } else {
          const balance = web3.utils.fromWei(result, "ether");
          setWalletBalance(financial(balance));
          console.log(financial(balance));
        }
      }
    );
  }, []);
  function financial(x) {
    return Number.parseFloat(x).toFixed(3);
  }
  return (
    <>
      <div className="flex flex-col min-h-screen overflow-hidden">
        <Header />
        <div>
          <div className="max-w-6xl  mt-24 mx-auto px-4 sm:px-6">
            {/* Top area: Blocks */}
            <div className="grid sm:grid-cols-12  px-6 gap-8 py-8 md:py-12">
              {/* 1st block */}
              <div className="sm:col-span-12 lg:col-span-4">
                <div>
                  <div class=" flex flex-wrap content-start ...">
                    <div
                      className="font-normal text-5xl tracking-tighter"
                      data-aos="zoom-y-out"
                    >
                      {WalletBalance}{" "}
                    </div>
                    <div
                      className="font-normal text-5xl tracking-tighter "
                      data-aos="zoom-y-out"
                    >
                      Îž
                    </div>
                    <div
                      className="text-base font-semibold
                      ml-3 tracking-tighter mt-3"
                      data-aos="zoom-y-out"
                    >
                      $20,790
                    </div>
                  </div>
                </div>
              </div>
              <div className="sm:col-span-12 md:col-span-3 lg:col-span-3 "></div>
              {/* 2nd block */}
              <div className=" text-center sm:col-span-12   md:col-span-3 lg:col-span-3 text-right">
                {/* <div class="inline-block h-20 w-20 rounded-full ring-2 ring-white border-4">
                  <Icons.ArrowUp
                    size={48}
                    style={{ marginLeft: 11, marginTop: 10 }}
                  />
                </div> */}
                <Icons.ArrowCircleDown weight="light" size={58} />
              </div>
            </div>
            {/*Divider Start */}
          </div>
        </div>
        {/* Start*/}

        <Footer />
      </div>
    </>
  );
}

export default Dashboard;
