import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { BrowserRouter as Router } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap";
import "../css/header.css";
import logoChimera from "../images/logoChimera.png";
import * as Icons from "phosphor-react";
import Dropdown from "react-bootstrap/Dropdown";

function Header() {
  const [top, setTop] = useState(true);
  const [open, setOpen] = React.useState(false);
  const history = useHistory();
  const handleToggle = () => {
    const div = document.querySelector("#check");
    if (div.classList.contains("show")) {
      setOpen((prevOpen) => !prevOpen);
    }
  };
  const SignOut = () => {
    localStorage.clear();
    history.push("/");
  };

  // detect whether user has scrolled the page down by 10px
  useEffect(() => {
    const scrollHandler = () => {
      window.pageYOffset > 10 ? setTop(false) : setTop(true);
    };
    window.addEventListener("scroll", scrollHandler);
    return () => window.removeEventListener("scroll", scrollHandler);
  }, [top]);
  return (
    <Router>
      <header
        className={`fixed  px-4 w-full bg-white-500 z-30 md:bg-opacity-90 transition duration-300 ease-in-out ${
          !top && " bg-red-100 blur shadow-lg"
        }`}
      >
        <Navbar expand="lg">
          <Navbar.Brand href="/">
            <img src={logoChimera} style={{ width: 130 }} />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link href="/editorial">Editorial</Nav.Link>
              <Nav.Link href="/discover">Discover</Nav.Link>
              <Nav.Link href="/market">Market</Nav.Link>
              <Nav.Link href="/activity">Activity</Nav.Link>
              {localStorage.getItem("walletAddress") ? (
                // <Nav.Link href="/editorial">Editorial</Nav.Link>
                <>
                  <Dropdown id="check">
                    <Dropdown.Toggle id="dropdown-basic">
                      <div class=" md:px-0 lg:px-0 flex flex-row ">
                        <div>
                          {" "}
                          <Icons.UserCircle
                            size={38}
                            color="black"
                            id="nav-avatar"
                          />
                        </div>
                        <div>
                          {" "}
                          {open ? (
                            <Icons.CaretUp size={25} color="black" />
                          ) : (
                            <Icons.CaretDown size={25} color="black" />
                          )}
                        </div>
                      </div>
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                      <Dropdown.Item href="/profile">Profile</Dropdown.Item>
                      <Dropdown.Item href="/dashboard">Dashboard</Dropdown.Item>
                      <Dropdown.Item href="/collection">
                        Collection
                      </Dropdown.Item>
                      <Dropdown.Divider />
                      <Dropdown.Item onClick={SignOut}>Sign out</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </>
              ) : (
                <Nav.Link href="/signin">SignIn</Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </header>
    </Router>
  );
}

export default Header;
