import React, { Component, useState, useEffect } from "react";

import { BrowserRouter as Router } from "react-router-dom";
import { Navbar, Nav } from "react-bootstrap";
import "./header.css";
import logoChimera from "../images/logoChimera.png";
import Button from "@material-ui/core/Button";
import ClickAwayListener from "@material-ui/core/ClickAwayListener";
import Grow from "@material-ui/core/Grow";
import Paper from "@material-ui/core/Paper";
import Popper from "@material-ui/core/Popper";
import MenuItem from "@material-ui/core/MenuItem";
import MenuList from "@material-ui/core/MenuList";
import { makeStyles } from "@material-ui/core/styles";
import * as Icons from "phosphor-react";
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  paper: {
    marginRight: theme.spacing(2),
  },
}));

function Header() {
  const [top, setTop] = useState(true);
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const anchorRef = React.useRef(null);
  const [down, SetDown] = useState(false);
  const handleToggle = () => {
    setOpen((prevOpen) => !prevOpen);
    SetDown((down) => !down);
  };

  const handleClose = (event) => {
    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return;
    }

    setOpen(false);
  };
  function handleListKeyDown(event) {
    if (event.key === "Tab") {
      event.preventDefault();
      setOpen(false);
    }
  }
  const prevOpen = React.useRef(open);
  React.useEffect(() => {
    if (prevOpen.current === true && open === false) {
      anchorRef.current.focus();
    }

    prevOpen.current = open;
  }, [open]);

  // detect whether user has scrolled the page down by 10px
  useEffect(() => {
    const scrollHandler = () => {
      window.pageYOffset > 10 ? setTop(false) : setTop(true);
    };
    window.addEventListener("scroll", scrollHandler);
    return () => window.removeEventListener("scroll", scrollHandler);
  }, [top]);
  return (
    <Router>
      <header
        className={`fixed  px-4 w-full bg-white-500 z-30 md:bg-opacity-90 transition duration-300 ease-in-out ${
          !top && " bg-red-100 blur shadow-lg"
        }`}
      >
        <Navbar expand="lg">
          <Navbar.Brand href="/">
            <img src={logoChimera} style={{ width: 130 }} />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link href="/editorial">Editorial</Nav.Link>
              <Nav.Link href="/discover">Discover</Nav.Link>
              <Nav.Link href="/market">Market</Nav.Link>
              <Nav.Link href="/news">News</Nav.Link>
              <Nav.Link href="/activity">Activity</Nav.Link>
              {localStorage.getItem("walletAddress") ? (
                // <Nav.Link href="/editorial">Editorial</Nav.Link>
                <div className={classes.root}>
                  <div>
                    <Button
                      ref={anchorRef}
                      aria-controls={open ? "menu-list-grow" : undefined}
                      aria-haspopup="true"
                      onClick={handleToggle}
                      className="flex"
                    >
                        <div class="space-x-4 ...">
                        <div class="inline-block ...">1</div>
                        <div class="inline-block ...">2</div>
                        <div class="inline-block ...">3</div>
                      </div>

                    </Button>
                    <Popper
                      open={open}
                      anchorEl={anchorRef.current}
                      role={undefined}
                      transition
                      disablePortal
                    >
                      {({ TransitionProps, placement }) => (
                        <Grow
                          {...TransitionProps}
                          style={{
                            transformOrigin:
                              placement === "bottom"
                                ? "center top"
                                : "center bottom",
                          }}
                        >
                          <Paper>
                            <ClickAwayListener onClickAway={handleClose}>
                              <MenuList
                                autoFocusItem={open}
                                id="menu-list-grow"
                                onKeyDown={handleListKeyDown}
                              >
                                <MenuItem>
                                  <a href="/check">Profile</a>
                                </MenuItem>
                                <MenuItem onClick={handleClose}>
                                  My account
                                </MenuItem>
                                <MenuItem onClick={handleClose}>
                                  Logout
                                </MenuItem>
                              </MenuList>
                            </ClickAwayListener>
                          </Paper>
                        </Grow>
                      )}
                    </Popper>
                  </div>
                </div>
              ) : (
                <Nav.Link href="/signin">SignIn</Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </header>
    </Router>
  );
}

export default Header;
