import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import config from "../config";
import Loader from "../partials/Loader";
const chimeraContract = require("../contracts/Chimera.json");
const SMAV2Contract = require("../contracts/ChimeraMarketAuctionV2.json");

let web3;
let accounts;
let chimera;
let SMAV2;

function DialoguePopUp(props) {
  const [open, setOpen] = React.useState(false);
  const [salePrice, setSalePrice] = useState("");
  const [validSalePrice, setValidSalePrice] = useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  function handleChangePrice(e) {
    setValidSalePrice(false);
    let value = e.target.value;
    if (/[^0-9]+/.test(value)) {
      value = value.replace(/[^0-9]*/g, "");
    }
    if (value < 0) {
      setValidSalePrice(true);
      setSalePrice(0);
    } else {
      setValidSalePrice(false);
      setSalePrice(value);
    }
  }
  async function onSetSalePrice(){
    if(salePrice && salePrice>0){

    }
    else{
      setValidSalePrice(true)
    }
  }

  return (
    <>
      <button
        className="btn btn-primary"
        onClick={handleClickOpen}
        style={{ fontSize: "12px" }}
      >
        Set SalePrice
      </button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Set Price of your NFT here"}
        </DialogTitle>
        <DialogContent className="text-center mb-5">
          <div className="px-5">
            {/* <a
              href="/create-digitalart"
              className="md:mr-5 lg:mr-5  xl:mr-5 sm:mr-0"
            >
              {props.id}
            </a> */}
            <Form>
              <Form.Group controlId="formBasicEmail">
                <Form.Label></Form.Label>
                <Form.Control
                  onChange={handleChangePrice}
                  type="number"
                  placeholder="Enter Price"
                  value={salePrice}
                />

                {validSalePrice ? (
                  <>
                    <Form.Text
                      className="text-muted"
                      style={{ color: "red", marginTop: "5px" }}
                    >
                      * The Price must be greater than 0
                    </Form.Text>
                  </>
                ) : null}
              </Form.Group>

              <Button variant="primary" type="submit" onClick={onSetSalePrice}>
                Submit
              </Button>
            </Form>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}
export default DialoguePopUp;
