import React, { useState, useEffect } from "react";
import Header from "../partials/Header";
import Footer from "../partials/Footer";
import MainDashboard from "../partials/Dashboard/MainDashboard";
import Web3 from "web3";
import * as Icons from "phosphor-react";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
let web3;

function Dashboard() {
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const [WalletBalance, setWalletBalance] = useState("");
  useEffect(() => {
    web3 = new Web3(window.ethereum);
    window.ethereum.enable();

    web3.eth.getBalance(
      localStorage.getItem("walletAddress"),
      function (err, result) {
        if (err) {
          console.log(err);
        } else {
          const balance = web3.utils.fromWei(result, "ether");
          setWalletBalance(financial(balance));
          console.log(financial(balance));
        }
      }
    );
  }, []);
  function financial(x) {
    return Number.parseFloat(x).toFixed(3);
  }
  return (
    <>
      <div className="flex flex-col min-h-screen overflow-hidden">
        <Header />
        <div className="px-8">
          <div className="max-w-10xl  mt-24 mx-auto px-4 sm:px-8">
            {/* Top area: Blocks */}

            <div className="grid sm:grid-cols-12  px-6 gap-8 py-8 md:py-12">
              {/* 1st block */}
              <div className="sm:col-span-12 lg:col-span-4">
                <div>
                  <div class=" flex  tracking-widest flex-wrap content-start ...">
                    <div
                      className="font-normal text-4xl sm:text-5xl "
                      data-aos="zoom-y-out"
                    >
                      {WalletBalance}{" "}
                    </div>
                    <div
                      className="font-normal text-4xl sm:text-5xl "
                      data-aos="zoom-y-out"
                    >
                      Ξ
                    </div>
                    <div
                      className="text-base font-semibold
                      ml-3 mt-3"
                      data-aos="zoom-y-out"
                    >
                      $20,790
                    </div>
                  </div>
                </div>
              </div>
              <div className="sm:col-span-12 md:col-span-3 text-center lg:col-span-3 "></div>

              <div className="sm:col-span-12 md:col-span-3   lg:col-span-3">
                <div className="text-center mb-20">
                  <button
                    onClick={handleClickOpen}
                    className="btn text-center add-artist-btn btn-primary mt-3 hover: bg-gray-1000"
                  >
                    MAKE AN ART{" "}
                    <i className="fas text-lg ml-2 fa-plus-circle"></i>
                  </button>
                  <Dialog
                    open={open}
                    onClose={handleClose}
                    aria-labelledby="alert-dialog-title"
                    aria-describedby="alert-dialog-description"
                  >
                    <DialogTitle id="alert-dialog-title">
                      {"Which One?"}
                    </DialogTitle>
                    <DialogContent className="text-center">
                      <div className="mr-5">
                        <button
                          className="btn btn-primary text-center ml-5 "
                          style={{
                            borderRadius: "30px",
                            height: 340,
                            width: 200,
                            backgroundColor: "white",
                            color: "black",
                            borderColor: "#D8D8D8",
                          }}
                        >
                          <img
                            src="https://rarible.com/static/2a78e39400f51f1dbeba13832f421092.png"
                            width="150px"
                            className="rounded shadow-2xl ml-2 mt-2 mb-3"
                          />
                          <span className="font">Digital</span>
                        </button>
                        <button
                          className="btn btn-primary text-center ml-5"
                          style={{
                            height: 300,
                            width: 200,
                            backgroundColor: "white",
                            color: "black",
                            borderColor: "gray",
                          }}
                        >
                          <img
                            src="https://rarible.com/static/48dc30c106da96755b60ead8627c8888.png"
                            width="150px"
                            className="rounded shadow-2xl ml-2"
                          />
                        </button>
                      </div>
                    </DialogContent>
                    <DialogActions>
                      <Button onClick={handleClose} color="primary">
                        Disagree
                      </Button>
                      <Button onClick={handleClose} color="primary" autoFocus>
                        Agree
                      </Button>
                    </DialogActions>
                  </Dialog>
                </div>
              </div>

              {/* 2nd block */}

              <div class=" md:px-0 lg:px-0 flex flex-row ">
                <div>
                  {" "}
                  <button>
                    <Icons.ArrowCircleDown
                      weight="thin"
                      color="black"
                      size={78}
                    />
                  </button>
                </div>
                <div>
                  {" "}
                  <button>
                    <Icons.ArrowCircleUp
                      weight="thin"
                      color="black"
