import React, { useState, useEffect } from "react";
import { MDBInput, MDBCol } from "mdbreact";
import Carousel from "react-bootstrap/Carousel";
import "../../css/curatedbrands.css";
import data from "../../Data/CuratedDetailed/data";
function MainDetailCurated(props) {
  const [data, setData] = useState("");
  useEffect(() => {
    setData(props.location.state.d);
    // var str =
    //   "https://ipfs.pixura.io/ipfs/QmYbPHZCpLZwk1nd1AWtcwZWpLsMfPGctGtL8HmMMcQhsD/wheels-are-turning.jpg";
    // var res = str.split(".");
    let result = [];
    for (let i = 0; i < data.length; i++) {
      result[i].data = data[i];
    }
    Promise.all(result).then((response) => {
      console.log(result);
    });
  }, []);

  return (
    <>
      {/* 3D */}
      <div
        className="max-w-6xl mt-40 text-center mx-auto px-4 sm:px-6 "
        data-aos="zoom-y-out"
      >
        <span className="font-Lobster italic text-5xl font-normal">
          Spinin Records
        </span>
      </div>
      <div className="max-w-8xl mt-10 text-center   mx-auto px-4 sm:px-6">
        <Carousel className="mt-20 shadow-2xl">
          <Carousel.Item className="changeColor">
            <div>
              <img
                src={data.src}
                class="object-cover  w-full ..."
                style={{ height: 430 }}
              />
            </div>

            <Carousel.Caption style={{ paddingBottom: 90 }}>
              <div className="row">
                <div className="col-md-3"></div>
                <div className="col-md-6 transparent">
                  <div style={{ paddingTop: 50, paddingBottom: 50 }}>
                    <h1>First slide label</h1>
                    <p>
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                    </p>
                  </div>
                </div>
              </div>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      </div>
      <section className="relative mt-40 px-8">
        {/* Section background (needs .relative class on parent and next sibling elements) */}

        <div className="relative max-w-8xl  px-10 sm:px-6">
          <div>
            {/* Items */}
            <div className="max-w-sm mx-auto grid gap-8 md:grid-cols-2 lg:grid-cols-3 md:max-w-2xl lg:max-w-none">
              {/* 1st item */}
              <div className=" mb-10 shadow-2xl flex flex-col bg-white ">
                <img
                  src="https://ipfs.pixura.io/ipfs/QmYbPHZCpLZwk1nd1AWtcwZWpLsMfPGctGtL8HmMMcQhsD/wheels-are-turning.jpg"
                  alt="Random Creativity Outburst"
                />
              </div>

              {/* 2nd item */}
              <div className="relative mb-10 shadow-2xl flex flex-col bg-white">
                <img
                  src="https://ipfs.pixura.io/ipfs/QmPrRbncSi243FsecPJHsgEL7DN5XcD8EPrUN7pF4R73ri/encounter.jpg"
                  class="recent-activity-item-img"
                  alt="Random Creativity Outburst"
                />
              </div>

              {/* 3rd item */}
              <div className="relative flex flex-col bg-white mb-10 shadow-2xl">
                <img
                  src="https://ipfs.pixura.io/ipfs/QmPN1CA11xbCjcTqVQEqpiRMMhxp9xHG4qV4BjQu5Xv26g/1703-ah03.png"
                  class="recent-activity-item-img"
                  alt="Random Creativity Outburst"
                />
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default MainDetailCurated;
